using StackExchange.Redis;
using System.Text;

public class RedisVectorIndex(IDatabase database)
{
    private readonly IDatabase _db = database;

    public List<HashEntry[]> SearchVectorIndex(string indexName, string queryVector, int topK, double? minPrice = null, double? maxPrice = null)
    {
        // Construct the price range query part
        string priceRangeQuery = minPrice.HasValue && maxPrice.HasValue ? $"@price:[{minPrice} {maxPrice}]" : "*";

        // Construct the search command
        var searchCommand = new List<object>
        {
            indexName,
            $"{priceRangeQuery}=>[KNN {topK} @image_vector $query_vector]",
            "PARAMS", "2",
            "query_vector", Convert.FromHexString(queryVector),
            "RETURN", "3", "__image_vector_score", "name", "price",
            "SORTBY", "__image_vector_score",
            "DIALECT", "2"
        };

        // Execute the search command
        RedisResult searchResults = _db.Execute("FT.SEARCH", searchCommand.ToArray());

        for (int i = 0; i < searchResults.Length; i++)
        {
            Console.WriteLine(searchResults[i]);
            for (int j = 0; j < searchResults[i].Length; j++)
            {
                Console.WriteLine(searchResults[i][j]);
            }
        }

        List<HashEntry[]> results = new List<HashEntry[]>();
        foreach (var result in results)
        {
            results.Add(result as HashEntry[]);
        }
        return results;
    }

    public void CreateVectorIndex(string indexName, string prefix, string schema, string indexType, int vectorDimension, string type, string distanceMetric, int initialCap, int? blockSize = null, int? m = null, int? efConstruction = null)
    {
        string indexCommand = indexType == "FLAT" ?
            $"{indexName} ON HASH PREFIX 1 {prefix}: SCHEMA {schema} image_vector VECTOR FLAT 10 TYPE FLOAT32 DIM {vectorDimension} DISTANCE_METRIC {distanceMetric} INITIAL_CAP {initialCap} BLOCK_SIZE {blockSize.GetValueOrDefault()}" :
            $"{indexName} ON HASH PREFIX 1 {prefix}: SCHEMA {schema} image_vector VECTOR HNSW 12 TYPE {type} DIM {vectorDimension} DISTANCE_METRIC {distanceMetric} INITIAL_CAP {initialCap} M {m.GetValueOrDefault()} EF_CONSTRUCTION {efConstruction.GetValueOrDefault()}";

        _db.Execute("FT.CREATE", indexCommand.Split(' '));
    }
    public void AddProduct(int key, string name, int price, string imageVector)
    {
        _db.HashSet("product:" + key,
        [
            new HashEntry("name", name),
            new HashEntry("price", price),
            new HashEntry("image_vector", Convert.FromHexString(imageVector))
        ]);
    }
}

class Program
{
    static async Task Main(string[] args)
    {
        ConfigurationOptions options = new ConfigurationOptions
        {
            EndPoints = { "" },
            Password = "",
            Ssl = false,
            AllowAdmin = true,
            ConnectTimeout = 5000,
            SyncTimeout = 5000
        };

        using (var connection = await ConnectionMultiplexer.ConnectAsync(options))
        {
            var cache = connection.GetDatabase();
            cache.Execute("FLUSHALL");
            RedisVectorIndex redisVectorIndex = new RedisVectorIndex(cache);

            // Example usage:
            // Create a FLAT index
            redisVectorIndex.CreateVectorIndex(
                "idx1",
                "product",
                "name TEXT price NUMERIC",
                "FLAT",
                512,
                "FLOAT32",
                "COSINE",
                5,
                5
            );

            // Create an HNSW index
            redisVectorIndex.CreateVectorIndex(
                "idx2",
                "product",
                "name TEXT price NUMERIC",
                "HNSW",
                512,
                "FLOAT32",
                "COSINE",
                5,
                5,
                20,
                100
            );

            // Add products
            redisVectorIndex.AddProduct(1, "Mobile phone cover", 50, "");
            redisVectorIndex.AddProduct(2, "Gold phone cover", 78, "");
            redisVectorIndex.AddProduct(3, "Patio Light", 120, "");
            redisVectorIndex.AddProduct(4, "2 -seater sofa", 400, "");
            redisVectorIndex.AddProduct(5, "Comfy chair", 200, "");

            var queryVec = "";

            // Method call for the first query (FLAT index without price filtering)
            Console.WriteLine("Results for the first query (FLAT index without price filtering):");
            var results1 = redisVectorIndex.SearchVectorIndex(
                "idx1",
                queryVec,
                2
            );

            // Method call for the second query (HNSW index without price filtering)
            Console.WriteLine("Results for the second query (HNSW index without price filtering):");
            var results2 = redisVectorIndex.SearchVectorIndex(
                "idx2",
                queryVec,
                2
            );

            // Method call for the third query (FLAT index with price filtering)
            Console.WriteLine("Results for the third query (FLAT index with price filtering):");
            var results3 = redisVectorIndex.SearchVectorIndex(
                "idx1",
                queryVec,
                2,
                70,
                80
            );

            // Method call for the fourth query (HNSW index with price filtering)
            Console.WriteLine("Results for the fourth query (HNSW index with price filtering):");
            var results4 = redisVectorIndex.SearchVectorIndex(
                "idx2",
                queryVec,
                2,
                70,
                80
            );

            Console.WriteLine("Finished!");
        }
    }
}
